import React, { useState, useEffect } from 'react';


const useFetch = (url) => {

    const [products, setProducts]= useState(null);

    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetch(url)
            .then(res => {
                return res.json()
            }).then(data => {
            setProducts(data)
            setIsLoading(false)
        })
        console.log("good")
    }, [url]);


    return{products, isLoading}
}

export default useFetch;
