import React, {useState} from "react";
import './Modal.css'

export const Modal = ({closeModal}) => {

    const[title, setTitle] = useState("")
    const[weight, setWeight] = useState("")
    const[price, setPrice] = useState("")
    const[url, setUrl] = useState("")

    const handleSubmit = (e) => {
        e.preventDefault();
        const productsUpdate ={title, weight, price, url}
        console.log(productsUpdate)
    }

    return(
        <div className="modal-block">
            <div className="modal-window">
                <div className="modal-top">
                    <h2>Here you can add knife to list</h2>
                    <button className="close-modal-btn" onClick={() => closeModal(false)}>X</button>
                </div>
                <div className="modal-middle">
                <div className="modal-form">
                    <form onSubmit={handleSubmit}>
                        <div className="modal-form-box">
                        <label>Product title</label>
                        <input
                            type="text"
                            required
                            value={title}
                            onChange={(e)=> setTitle(e.target.value)}
                        />
                        </div>
                        <div className="modal-form-box">
                        <label>Product weight</label>
                        <input
                            type="text"
                            required
                            value={weight}
                            onChange={(e)=> setWeight(e.target.value)}
                        />
                        </div>
                        <div className="modal-form-box">
                        <label>Product price $</label>
                        <input
                            type="number"
                            required
                            value={price}
                            onChange={(e)=> setPrice(e.target.value)}
                        />
                        </div>
                        <div className="modal-form-box">
                        <label>Product image(URL)</label>
                        <textarea
                            required
                            value={url}
                            onChange={(e)=> setUrl(e.target.value)}
                        />
                        </div>
                    </form>
                </div>
                </div>
                <div className="modal-bottom">
                    <button className="modal-btn-add">Add</button>
                </div>
            </div>
        </div>
    )
}
