import React, {useEffect, useState} from "react";
import './ModalUpdate.css'
import {Link, useParams} from "react-router-dom";
import useFetch from "../../../custom-hooks/UseFetch";

export const ModalUpdate = ({closeModal}) => {

    const { id } = useParams()
    // const {products} = useFetch('http://localhost:3001/products/' + id)
    useEffect(() => {
        fetch('http://localhost:3001/products/' + id)
            .then(res => {
                return res.json()
            }).then(data => {
            setProducts(data)
        })
    }, );

    const [products, setProducts]= useState({});
    console.log(products)

    const[name, setName] = useState("");
    const[weight, setWeight] = useState("");
    const[price, setPrice] = useState("");
    const[imageUrl, setImageUrl] = useState("");


    // const initializingData = () => {
    //     setName(products)
    // }
    //
    //

    const updateName =(nameUpdated) => {
        setName(products.name)
    }


    const editSubmit = (e) => {
        e.preventDefault();

        const productsUpdate ={name, weight, price, imageUrl,}
        // console.log(name)
        // console.log(weight)
        // console.log(price)
        // console.log(imageUrl)


        fetch('http://localhost:3001/products/' + id, {
            method: 'PATCH',
            headers: {
                "Accept":"application/json",
                "Content-Type":"application/json",
            },
            body: JSON.stringify(productsUpdate)
        }).then( () => {
            console.log('new product added')
            window.location.reload();
        })
    }

    return (
         <div className="modal-block">
             {products && (
                 <div className="modal-window">
                     <div className="modal-top">
                         <h2>Here you can update information</h2>
                         <button className="close-modal-btn" onClick={() => closeModal(false)}>X</button>
                     </div>
                     <div className="modal-middle">
                         <div className="modal-form">
                             <form onSubmit={editSubmit}>
                                 <div className="modal-form-box">
                                     <label>Product title</label>
                                     <input
                                         type="text"
                                         value={products.name}
                                         // onClick={()=> {
                                         //    setName(products.name)
                                         // }}
                                         onChange={(e) =>products.name = setName(e.target.value)}

                                     />
                                 </div>
                                 <div className="modal-form-box">
                                     <label>Product weight</label>
                                     <input
                                         type="text"
                                         value={products.weight}
                                         onChange={(e) => products.weight = setWeight(e.target.value)}
                                     />
                                 </div>
                                 <div className="modal-form-box">
                                     <label>Product price $</label>
                                     <input
                                         type="number"
                                         value={products.price}
                                         onChange={(e) => products.price = setPrice(e.target.value)}
                                     />
                                 </div>
                                 <div className="modal-form-box">
                                     <label>Product image(URL)</label>
                                     <textarea
                                         value={products.imageUrl}
                                         onChange={(e) => products.imageUrl = setImageUrl(e.target.value)}
                                     />
                                 </div>
                                 <div className="modal-button-box">
                                     <button className="modal-btn-add">Add</button>
                                 </div>
                             </form>
                         </div>
                     </div>
                 </div>
             )}
            </div>
        )
}
